import React from 'react';
import DatePicker from 'react-datepicker';
import { jobsData } from './sampleData/sampleData.js'
import './JobEntry.css';

function JobEntry({ job, onChange, onDateChange }) {
    // Array of Job Objects
    const handleChange = (index, field, value) => {
        setJobs(prevJobs => prevJobs.map((job, idx) => idx === index ? {...job, [field]: value} : job));
    };

    // Handler for text changes on job fields like jobTitle, company, etc.
    const handleFieldChange = (index, field, value) => {
    const newJobs = jobs.map((job, idx) => {
        if (idx === index) {
            return { ...job, [field]: value };
        }
        return job;
    });
    setJobs(newJobs);
    };

    // Handler for date changes
    const handleDateChange = (index, field, date) => {
        const newJobs = jobs.map((job, idx) => {
            if (idx === index) {
                return { ...job, [field]: date };
            }
            return job;
        });
        setJobs(newJobs);
    };

    return (
    <form className={`experienceInput ${job.isVisible ? '' : 'hidden'}`}>
        <div className="detailItem">
            <label className="experienceLabel">Job Title</label>
            <input
                className="inputField"
                type="text"
                name="jobTitle"
                value={job.jobTitle}
                onChange={e => onChange(e.target.name, e.target.value)}
            />
        </div>
        <div className="detailItem">
            <label className="experienceLabel">Company</label>
            <input
                className="inputField"
                type="text"
                name="company"
                value={job.company}
                onChange={e => onChange(e.target.name, e.target.value)}
            />
        </div>
        <div className="detailItem">
            <label className="experienceLabel">Start Date</label>
            <DatePicker
                className="inputField"
                selected={job.startDate}
                onChange={date => onDateChange('startDate', date)}
                dateFormat="MMMM yyyy"
                showMonthYearPicker
            />
        </div>
        <div className="detailItem">
            <label className="experienceLabel">End Date</label>
            <DatePicker
                className="inputField"
                selected={job.endDate}
                onChange={date => onDateChange('endDate', date)}
                dateFormat="MMMM yyyy"
                showMonthYearPicker
            />
        </div>
        <div className="detailItem">
            <label className="experienceLabel">Description</label>
            <textarea
                className="inputField descriptionInput"
                name="description"
                value={job.description}
                onChange={e => onChange(e.target.name, e.target.value)}
                rows="4"
            />
        </div>
    </form>
  );
}

export default JobEntry;
