import React from 'react';
import './ExperienceHeader.css';
import '../CommonStyles/styles.css';

export default function ExperienceHeader({ headerText, isVisible, toggle }) {
    return (
        <div className={`chevronIcon ${isVisible ? 'visible' : 'hidden'}`}>
            <h2 className="LHSInputHeader" onClick={toggle}>{headerText}</h2>
        </div>
    );
}
